mapper.select:
select distinct FROM love_blog_media_type o WHERE o.STATUS !='DELETED'
and o.CODE like '%${code}%'
and o.NAME like '%${name}%'
and o.isvalid = #{isvalid}
and o.isshow = #{isshow}
order by o.display asc

select count(distinct o.id) FROM love_blog_media_type o WHERE o.STATUS !='DELETED'
and o.CODE like '%${code}%'
and o.NAME like '%${name}%'
and o.isvalid = #{isvalid}
and o.isshow = #{isshow}

		select 
		from love_blog_media_type o
		where o.CODE=#{code} 
		 
            	and o.ID!=#{id}

		select 
		from love_blog_media_type o
		where o.NAME=#{name} 
		 
            	and o.ID!=#{id}

		select 
		from love_blog_media_type o
		where o.display=#{display} 
		 
            	and o.ID!=#{id}

		select 
		FROM love_blog_media_type o
		where 1=1
         
            and o.status=#{status}
         
         
            and o.isvalid=#{isvalid}

select FROM love_blog_media_type o where o.status='DEFAULT' and o.isvalid='Y'
and o.isshow=#{isshow}
order by o.display asc
