mapper.update:
update love_system_auth
  
          
           CODE= #{code},  
          
          
          NAME = #{name},
          
          
          CREATE_TIME = #{createTime},
        
          
          MODIFY_TIME = #{modifyTime},
        
          
          STATUS = #{status},
        
          
         ISVALID = #{isvalid}
        
          
         menu_id = #{menuId}
        
      
where ID=#{id}

mapper.select:
select o.ID,o.CODE,o.NAME,o.CREATE_TIME,o.MODIFY_TIME,o.STATUS,o.ISVALID,o.menu_id,m.name as menuName
from love_system_auth o,love_system_menu m
where o.menu_id = m.id and o.ISVALID ='Y'

	and m.ID=#{menuId}


	and o.STATUS=#{status}

select o.ID,o.CODE,o.NAME,o.CREATE_TIME,o.MODIFY_TIME,o.STATUS,o.ISVALID,o.menu_id,m.name as menuName
from love_system_auth o , love_system_r_user_auth r,love_system_menu m
where o.menu_id = m.id and o.ISVALID ='Y' and o.ID=r.AUTH_ID and r.USER_ID=#{userId} 

	and m.ID=#{menuId}


	and o.STATUS=#{status}

		select o.ID,o.CODE,o.NAME,o.CREATE_TIME,o.MODIFY_TIME,o.STATUS,o.ISVALID,o.menu_id
		from love_system_auth o
		where o.CODE=#{code} 
		 
            	and o.ID!=#{id}

		select o.ID,o.CODE,o.NAME,o.CREATE_TIME,o.MODIFY_TIME,o.STATUS,o.ISVALID,o.menu_id
		from love_system_auth o
		where o.NAME=#{name} 
		 
            	and o.ID!=#{id}

select o.ID,o.CODE,o.NAME,o.STATUS,o.ISVALID,o.CREATE_TIME,o.MODIFY_TIME,o.menu_id,m.name as menuName
from love_system_auth o,love_system_r_role_auth r,love_system_menu m
where o.menu_id = m.id and o.ID=r.AUTH_ID and o.ISVALID='Y' and o.STATUS !='DELETED' and r.ROLE_ID=#{roleId}
